http://api.picuploader.com {
        root * /var/www/picuploader

        php_fastcgi * unix//run/php-fpm/php-fpm.sock {
                index dashboard.php
        }

        file_server {
                index index.php
        }

        handle_errors {
        root * /etc/caddy/error
                rewrite * /error.html
                templates
                file_server
        }
}

# Import additional caddy config files in /etc/caddy/conf.d/
import /etc/caddy/conf.d/*
