export default {
    async fetch(request, env, ctx) {
      return new Response('Hello World!');
    },

    async scheduled(event, env, ctx) {
        const data = await fetchCopr()
        const errorPackages = new Array()

        for (const pkg of data.packages) {
            for (const chroot of Object.values(pkg.chroots)) {
                if (chroot.state == "failed") {
                    errorPackages.push(pkg.name)
                    break
                }
            }
        }

        if (errorPackages.length > 0) {
            await notify(`COPR 以下包发生构建失败:\n${errorPackages.join("\n")}`)
        } else {
            console.log("COPR 所有包构建成功")
        }
    }
};

addEventListener('fetch', event => {
  const url = new URL(event.request.url)
  if (url.pathname === WEBHOOK) {
    event.respondWith(handleWebhook(event))
  } else if (url.pathname === '/registerWebhook') {
    event.respondWith(registerWebhook(event, url, WEBHOOK, SECRET))
  } else if (url.pathname === '/unRegisterWebhook') {
    event.respondWith(unRegisterWebhook(event))
  } else {
    event.respondWith(new Response('No handler for this request'))
  }
})

/**
 * Handle requests to WEBHOOK
 * https://core.telegram.org/bots/api#update
 */
async function handleWebhook (event) {
  // Check secret
  if (event.request.headers.get('X-Telegram-Bot-Api-Secret-Token') !== SECRET) {
    return new Response('Unauthorized', { status: 403 })
  }

  // Read request body synchronously
  const update = await event.request.json()
  // Deal with response asynchronously
  event.waitUntil(onUpdate(update))

  return new Response('Ok')
}

/**
 * Handle incoming Update
 * https://core.telegram.org/bots/api#update
 */
async function onUpdate (update) {
  if ('message' in update) {
    await onMessage(update.message)
  }
}

/**
 * Handle incoming Message
 * https://core.telegram.org/bots/api#message
 */
function onMessage (message) {
  return sendPlainText(message.chat.id, 'Echo:\n' + message.text)
}

/**
 * Send plain text message
 * https://core.telegram.org/bots/api#sendmessage
 */
async function sendPlainText (chatId, text) {
  return (await fetch(apiUrl('sendMessage', {
    chat_id: chatId,
    text
  }))).json()

async function onMessage(message) {
    if (!message.text) {
        return;
    }
    const b23Reg = /b23\.tv\/[a-zA-Z0-9]+/g;
    if (!b23Reg.test(message.text)) {
        console.log('No match found');
    }
    const b23Links = message.text.match(b23Reg);
    const cleanLinks = [];
    await Promise.all(
        b23Links.map(link => b23Remover('https://' + link))
    ).then(result => {
        cleanLinks.push(...result);
    });

    const text2Send = "Track ID removed:\n" + cleanLinks.join("\n");
    return sendPlainText(message.chat.id, text2Send);
}

async function b23Remover (url) {
    const r = await fetch(url)
    const v = r.url
    const u = new URL(v)
    return u.origin + u.pathname
}

