const Application = require('@waline/vercel');

module.exports = Application({
    plugins: [
        {
            middlewares: [
                async (ctx, next) => {
                    const referer = ctx.request.headers['referer'];
                    if (referer) {
                        if (
                            !referer.include('localhost') &&
                            !referer.include('127.0.0.1') &&
                            !referer.include('zhul.in')
                        ) {
                            ctx.status = 403
                            ctx.body = 'Forbidden'
                            return
                        }
                    }
                    await next();
                },
            ]
        }
