const a = {
    x: 3,
    u1: function () {
        console.log(this.x)
    },
    u2: () => {
        console.log(this.x)
    }
}

class b {
    x = 3

    u1 = function () {
        console.log(this.x)
    }

    u2 = () => {
        console.log(this.x)
    }
}

a.u1()
// 3
a.u2()
// undefined

new b().u1()
// 3
new b().u2()
// 3

export default () => {
  process.env = { ...process.env, ...loadEnv(process.cwd(),'') };

  const config = {
    plugins: [vue()],
    server: {
      proxy: {
        '/api': {
          target: http://127.0.0.1:8080,
          changeOrigin: true,
          secure: false,
        }
      }
    }
  }
  return defineConfig(config)
};
